function Button() {
  const [Value, setValue] = useState({
    one: " ",
  });

  let counter = 1;
  const updateValue = () => {
    if (counter % 2=== 0) {
      setValue((previousState) => {
        return { ...previousState, one: "X" };
      });
    } else {
      setValue((previousState) => {
        return { ...previousState, one: "O" };
      });
    }
  };

  return (
    <button
      onClick={updateValue}
      className="text-[150px] text-lime-300 h-full w-full"
    >
      {Value.one}
    </button>
  );
}

export default Button;
