import { useState } from "react"


function Border(){
    // const [cell,setCell] =useState(['','','','','','','','',''])
    const [Value,setValue] = useState({
        one:' ',
    })

    let counter=1;
    const updateValue = () => {
        if(counter%2==0)
        {setValue(previousState => {
          return { ...previousState, one: "X" }
        })
        }else{
            setValue(previousState => {
                return { ...previousState, one: "O" }
              });
        }
        counter++
        alert(counter)
      }

      
    
    return (
        <div className='flex items-center justify-center w-100 h-screen bg-black'>
          <table className=' w-1/2 h-1/2 border-collapse '>
            <tr className='h-1/3 border-b-8'>
            <th className='w-1/3'>
            <button onClick={updateValue} className='text-[150px] text-lime-300 h-full w-full'>{Value.one}</button>
            </th>
            <th className='border-l-8 w-1/3'>
            <button onClick={updateValue} className='text-[150px] text-lime-300 h-full w-full'>{Value.one}</button>
            </th>
            <th className='border-l-8 w-1/3 '>
            <button onClick={updateValue} className='text-[150px] text-lime-300 h-full w-full'>{Value.one}</button></th>
            </tr>
    
            <tr className='border-b-8 h-1/3'>
            <th className='w-1/3'>
            <button className='h-full w-full'></button></th>
            <th className='border-l-8 w-1/3'>
            <button className='h-full w-full'></button></th>
            <th className='border-l-8 w-1/3'>
            <button className='h-full w-full'></button></th>
            </tr>
    
            <tr className='h-1/3'>
            <th className='w-1/3'>
            <button className='h-full w-full'></button></th>
            <th className='border-l-8 w-1/3'>
            <button className='h-full w-full'></button></th>
            <th className='border-l-8 w-1/3'>
            <button className='h-full w-full'></button></th>
            </tr>
          </table>
    
        </div>
      )
}

export default Border
